import * as React from 'react';
import Avatar from '@mui/material/Avatar';
import Button from '@mui/material/Button';
import CssBaseline from '@mui/material/CssBaseline';
import TextField from '@mui/material/TextField';
import Link from '@mui/material/Link';
import Grid from '@mui/material/Grid';
import Box from '@mui/material/Box';
import LockOutlinedIcon from '@mui/icons-material/LockOutlined';
import Typography from '@mui/material/Typography';
import Container from '@mui/material/Container';
import { createTheme, ThemeProvider } from '@mui/material/styles';
import { useState } from 'react';
import Axios from 'axios';
import { Navigate, useLocation, useNavigate } from "react-router-dom";

function Copyright(props) {
    return (
        <Typography variant="body2" color="text.secondary" align="center" {...props}>
            {'Copyright © '}
            <Link color="inherit" href="https://mui.com/">
                ScheduleSwift
            </Link>{' '}
            {new Date().getFullYear()}
            {'.'}
        </Typography>
    );
}

const theme = createTheme();

const CustomerConfirmAccount = () => {
    const [loginStatus, setLoginStatus] = useState('');
    const [email, setEmail] = useState('');
    const navigate = useNavigate();
    const handleSubmit = (event) => {
        event.preventDefault();
        const data = new FormData(event.currentTarget);
    }
    //     Axios.post("http://localhost:3001/api/customerConfirmAccount", {
    //         email: data.get('email'),
    //         username: data.get('username'),
    //         confirmNum: data.get('confirmNum'),
    //     }).then((result) => {
    //         if (result.data.message) {
    //             setLoginStatus(result.data.message);
    //         } else {
    //             navigate("/customerChangePassword", {
    //                 state: {
    //                     username: data.get('username'),
    //                     email: data.get('email'),
    //                 }
    //             });
    //         }
    //     })
    //     console.log({
    //         email: data.get('email'),
    //         username: data.get('username'),
    //     });
    // };

    // function showEmail(e){
    //     var email = 'jjennyha18@gmail.com';
    //     document.getElement
    //     document.getElementById("show").innerText = firstName+" "+lastName;        
    // }

    return (
        <ThemeProvider theme={theme}>
            <Container component="main" maxWidth="xs">
                <CssBaseline />
                <Box
                    sx={{
                        marginTop: 8,
                        display: 'flex',
                        flexDirection: 'column',
                        alignItems: 'center',
                    }}
                >
                    <Avatar sx={{ m: 1, bgcolor: 'secondary.main' }}><LockOutlinedIcon /></Avatar>
                    <Typography component="h1" variant="h5">Confirm Account</Typography>
                    <Typography justifyContent="flex-end" component="h1" variant="body2">
                        A confirmation link has been sent to</Typography>
                        '<span>'data.get('email')</span>'
                        
                        Click on the link in order to activate your account. Once your account is activated, you will
                        be automatically redirected to the main page.
                        If you did not receive an email, click "Resend Confirmation Email" and an email with the confirmation link will be re-sent.
                    {/* </Typography> */}
                    <Button
                        type="submit"
                        fullWidth
                        variant="contained"
                        sx={{ mt: 3, mb: 2 }}
                    >
                    Resend Confirmation Email
                    </Button>
                    <Copyright sx={{ mt: 5 }} />
                </Box>
            </Container>
        </ThemeProvider>
    );
}
export default CustomerConfirmAccount;
